def get_balance(user_id):
    if not os.path.exists(BALANCE_FILE):
        return 0
    with open(BALANCE_FILE, "r", encoding="utf-8") as f:
        try:
            balances = json.load(f)
        except json.JSONDecodeError:
            balances = {}
    return balances.get(str(user_id), 0)
